import React, { useState } from 'react';
import styled from 'styled-components';

const IconContainer = styled.div`
  cursor: pointer;
`;
const wrapperComponentMethod = (
  Icon1: React.FC,
  Icon2: React.FC,
  { callToAction }: { callToAction: (state: object) => void }
) => () => {
  const [iconVisiblity, setVisible] = useState(false);
  const iconCTA = () => {
    setVisible(!iconVisiblity);
    callToAction({ state: !iconVisiblity });
  };
  return (
    <IconContainer onClick={iconCTA}>
      {!iconVisiblity && <Icon1 />}
      {iconVisiblity && <Icon2 />}
    </IconContainer>
  );
};

export default wrapperComponentMethod;
