import React from 'react';
import styled from 'styled-components';
import { VisibilityIcon } from '@mono-nx-test-with-nextjs/ui';
import FavoriteIcon from '@material-ui/icons/Favorite';

//custom
import RatingStarComp from '../RatingStarComp/rating';

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: red;
  /* align-items: center; */
  min-width: 200px;
  min-height: 300px;
  border-radius: 20px;
  /* justify-content: center; */
  margin: 10px;
  -webkit-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  -moz-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
`;
const Poster = styled.div`
  border-radius: 10px;
  width: 150px;
  margin: 0 auto;
`;

const PosterImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding: 10px;
`;

const CardFooter = styled.div`
  width: 100%;
`;

/* ... inside the render or return of your component ... */

interface Props {
  Title: string;
  Poster: string;
}
function Card(props: Props) {
  const { Title, Poster: posterURL } = props;
  return (
    <CardContainer>
      <CardHeader>
        <VisibilityIcon />
        <FavoriteIcon />
      </CardHeader>
      <Poster>
        <PosterImage src={posterURL} alt={Title} />
      </Poster>
      <CardFooter>
        <RatingStarComp />
      </CardFooter>
    </CardContainer>
  );
}

export default Card;
