import React from 'react';
import { VisibilityIcon } from '@mono-nx-test-with-nextjs/ui';
import VisibilityOffOutlinedIcon from '@material-ui/icons/VisibilityOffOutlined';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';

//custom
import wrapperIconHOC from '../Card/IconCTA/IconAction';
const VisibilityWatchedIcon = wrapperIconHOC(
  VisibilityIcon,
  VisibilityOffOutlinedIcon
);
const VisibilitySavedIcon = wrapperIconHOC(FavoriteBorderIcon, FavoriteIcon);

function IconsStateManagement() {
  interface IconStateInt {
    watch: boolean;
    favorite: boolean;
  }

  let prevState: IconStateInt;
  let calculatedState: IconStateInt;
  const callToAction = (iconState: object) => {
    const watch = { watch: false, ...iconState };
    const favorite = { favorite: false, ...iconState };
    calculatedState = { ...watch, ...favorite };
    if (prevState) {
      calculatedState = { ...prevState, ...iconState };
    }

    prevState = calculatedState;

    console.log('iconsState', calculatedState);
  };
  return (
    <>
      <VisibilityWatchedIcon
        callToAction={callToAction}
        icon="watch"
        obj={{
          watch: false,
          favorite: false,
        }}
        cardNumber={'3'}
      />
      <VisibilitySavedIcon
        callToAction={callToAction}
        icon="favorite"
        obj={{
          watch: false,
          favorite: false,
        }}
        cardNumber={'4'}
      />
    </>
  );
}

export default IconsStateManagement;
