import React, { useEffect, useState, useCallback, useReducer } from 'react';
import styled from 'styled-components';

//custom
import Card from '../app/contentComponents/Card/Card';
import generalAPIService from '../app/services/apiService';
import FiltersGroup from '../app/contentComponents/FiltersGroup/FiltersGroup';

const Wrapper = styled.div`
  display: flex;
  width: 100%;
`;

const Filters = styled.div`
  display: flex;
  width: 20%;
  background-color: grey;
`;
const Movies = styled.div`
  display: flex;
  width: 80%;
  flex-wrap: wrap;
`;

interface ImoviesResponseFields {
  Title: string;
  Poster: string;
  Ratings: {}[];
  key: string;
  Saved: string;
  Watched: string;
  Type: 'movie' | 'series';
}
const initialState = {
  movies: [
    {
      Title: 'string',
      Poster: 'string',
      Ratings: [{}],
      key: 'string',
      Saved: 'True',
      Watched: 'False',
      Type: 'movie',
    },
  ],
  updateState: (state) => state,
};
export const MoviesContext = React.createContext(initialState);
const { Provider, Consumer } = MoviesContext;

const movieReducer = ({ movies }, action) => {
  switch (action.type) {
    case 'UPDATE_FROM_FETCH':
      return [
        ...action.payload.filter(({ Watched }) =>
          JSON.parse(Watched.toLowerCase())
        ),
      ];
    case 'FILTER_WATCHED':
      return movies.filter(({ Watched }) => Watched === action.payload);

    default:
      return movies;
  }
};

const Home = () => {
  const [loading, isLoading] = useState(true);
  //const [movies, getMovies] = useState([]);
  const [store, dispatch] = useReducer(movieReducer, initialState);

  const reduceData = useCallback((response: {}[]) => {
    return response.map(
      ({ Title, Poster, Ratings, Watched, Saved }: ImoviesResponseFields) => {
        return {
          Title,
          Poster,
          Ratings,
          Watched,
          Saved,
        };
      }
    );
  }, []);

  useEffect(() => {
    isLoading(true);
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      dispatch({ type: 'UPDATE_FROM_FETCH', payload: reduceData(result) });
      isLoading(false);
    };

    fetchData();
  }, [reduceData]);

  console.log('gagdg', store);

  return (
    <main>
      {loading && 'movies are loading'}
      {!loading && (
        <Provider value={store}>
          <Wrapper>
            <Filters>
              <FiltersGroup />
            </Filters>
            <Movies>
              <Consumer>
                {({ movies }) =>
                  movies.map((fields, i) => (
                    <Card key={fields.Title} cardId={i} />
                  ))
                }
              </Consumer>
            </Movies>
          </Wrapper>
        </Provider>
      )}
    </main>
  );
};

export default Home;
