import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

//custom
import Card from './Card/Card';
import generalAPIService from './services/apiService';

const Movies = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Home = () => {
  const [movies, getMovies] = useState([]);

  interface ImoviesResponseFields {
    Title: string;
    Poster: string;
    key: string;
  }

  const reduceData = (response: {}[]) => {
    return response.map(({ Title, Poster }: ImoviesResponseFields) => {
      return { Title, Poster };
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      console.log('result', result);
      getMovies(reduceData(result));
    };

    fetchData();
  }, []);

  return (
    <main>
      <Movies>
        {movies.map((fields) => (
          <Card {...fields} key={fields.Title} />
        ))}
      </Movies>
    </main>
  );
};

export default Home;
