import React, { useState } from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';

// custom
const InputComp = (props: { key: string }) => <input type="text" {...props} />;

const style: React.CSSProperties = {
  display: 'flex',
  flexDirection: 'column',
  padding: '10px',
  border: '1px dotted green',
};
/* .filter((e, i) => i > 4)} */
const all = Object.entries(generateFilters());
const displayed = all.slice(0, 5);
const hidden = all.slice(5, 50);

const FilterContainer = styled.div`
  displahy: flex;
  flexdirection: column;
`;

function FiltersGroup() {
  return (
    <FilterContainer>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true}>
          <div>
            {displayed.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </div>
        </Accordion>
        {hidden.length > 0 && (
          <Accordion>
            {hidden.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </Accordion>
        )}
      </div>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true}>
          <div>{displayed}</div>
        </Accordion>
        {hidden.length > 0 && (
          <Accordion>
            <div>{hidden}</div>
          </Accordion>
        )}
      </div>
    </FilterContainer>
  );
}

export default FiltersGroup;
