import React, { useEffect, useState, useCallback } from 'react';
import styled from 'styled-components';

//custom
import Card from '../app/contentComponents/Card/Card';
import generalAPIService from '../app/services/apiService';

const Wrapper = styled.div`
  display: flex;
  width: 100%;
`;

const Filters = styled.div`
  display: flex;
  width: 20%;
  background-color: grey;
`;
const Movies = styled.div`
  display: flex;
  width: 80%;
  flex-wrap: wrap;
`;

interface ImoviesResponseFields {
  Title: string;
  Poster: string;
  Ratings: {}[];
  key: string;
  Saved: string;
  Watched: string;
}
export const MoviesContext = React.createContext({} as ImoviesResponseFields[]);
const { Provider } = MoviesContext;

const Home = () => {
  const [loading, isLoading] = useState(true);
  const [movies, getMovies] = useState([]);

  const reduceData = useCallback((response: {}[]) => {
    return response.map(
      ({ Title, Poster, Ratings, Watched, Saved }: ImoviesResponseFields) => {
        isLoading(false);
        return {
          Title,
          Poster,
          Ratings,
          Watched,
          Saved,
        };
      }
    );
  }, []);

  useEffect(() => {
    isLoading(true);
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      getMovies(reduceData(result));
    };

    fetchData();
  }, [reduceData]);

  return (
    <main>
      {loading && 'movies are loading'}
      <Provider value={movies}>
        <Wrapper>
          <Filters />
          <Movies>
            {movies.map((fields, i) => (
              <Card key={fields.Title} cardId={i} />
            ))}
          </Movies>
        </Wrapper>
      </Provider>
    </main>
  );
};

export default Home;
