import React, { useState } from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';

const all = Object.entries(generateFilters());
const displayed = all.slice(0, 5);
const hidden = all.slice(5, all.length);

const FilterContainer = styled.div`
  display: flex;
  flex-direction: column;
`;
const AccordionMore = styled.div`
  padding: 10px;
`;

function FiltersGroup() {
  return (
    <FilterContainer>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true}>
          <div>
            {displayed.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </div>
        </Accordion>
        {hidden.length > 0 && (
          <AccordionMore>
            <Accordion>
              {hidden.map(([key, value]) => (
                <div key={key}>
                  <input type="radio" />
                  {`${key}(${value})`}
                </div>
              ))}
            </Accordion>
          </AccordionMore>
        )}
      </div>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true}>
          <div>
            {displayed.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </div>
        </Accordion>
        {hidden.length > 0 && (
          <AccordionMore>
            <Accordion>
              {hidden.map(([key, value]) => (
                <div key={key}>
                  <input type="radio" />
                  {`${key}(${value})`}
                </div>
              ))}
            </Accordion>
          </AccordionMore>
        )}
      </div>
    </FilterContainer>
  );
}

export default FiltersGroup;
