import React, { useEffect, useState, useCallback, useReducer } from 'react';
import styled from 'styled-components';

//custom
import Card from '../app/contentComponents/Card/Card';
import generalAPIService from '../app/services/apiService';
import FiltersGroup from '../app/contentComponents/FiltersGroup/FiltersGroup';

const Wrapper = styled.div`
  display: flex;
  width: 100%;
`;

const Filters = styled.div`
  display: flex;
  width: 20%;
  background-color: grey;
`;
const Movies = styled.div`
  display: flex;
  width: 80%;
  flex-wrap: wrap;
`;

interface ImoviesResponseFields {
  Title: string;
  Poster: string;
  Ratings: {}[];
  key: string;
  Saved: string;
  Watched: string;
}
const initialState: { movies: ImoviesResponseFields[] } = {
  movies: [
    {
      Title: 'string',
      Poster: 'string',
      Ratings: [{}],
      key: 'string',
      Saved: 'True',
      Watched: 'False',
    },
  ],
};
export const MoviesContext = React.createContext({} as ImoviesResponseFields[]);
const { Provider, Consumer } = MoviesContext;

const movieReducer = (state: ImoviesResponseFields[], action) => {
  switch (action.type) {
    case 'UPDATE_FROM_FETCH':
      return [
        ...action.payload.filter(({ Watched }) =>
          JSON.parse(Watched.toLowerCase())
        ),
      ];
    case 'FILTER_WATCHED':
      return state.filter(({ Watched }) => Watched === action.payload);

    default:
      return state;
  }
};

const Home = () => {
  const [loading, isLoading] = useState(true);
  //const [movies, getMovies] = useState([]);
  const [store, dispatch] = useReducer(movieReducer, initialState.movies);

  const reduceData = useCallback((response: {}[]) => {
    return response.map(
      ({ Title, Poster, Ratings, Watched, Saved }: ImoviesResponseFields) => {
        return {
          Title,
          Poster,
          Ratings,
          Watched,
          Saved,
        };
      }
    );
  }, []);

  useEffect(() => {
    isLoading(true);
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      dispatch({ type: 'UPDATE_FROM_FETCH', payload: reduceData(result) });
      isLoading(false);
    };

    fetchData();
  }, [reduceData]);

  console.log('gagdg', store);

  return (
    <main>
      {loading && 'movies are loading'}
      {!loading && (
        <Provider value={store}>
          <Wrapper>
            <Filters>
              <FiltersGroup />
            </Filters>
            <Movies>
              <Consumer>
                {(moviesObj) =>
                  moviesObj.map((fields, i) => (
                    <Card key={fields.Title} cardId={i} />
                  ))
                }
              </Consumer>
            </Movies>
          </Wrapper>
        </Provider>
      )}
    </main>
  );
};

export default Home;
