import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
//custom
import RatingStarComp from '../RatingStarComp/Rating';
import IconsStateManagement from '../IconsManagement/IconsStateManagement';
import { MoviesContext } from '../index';

const cardColorMapping = {
  default: '#FFF',
  savedNotWatched: '#ED6606',
  watchedAndSaved: '#049452',
  watchedNotSaved: '#EEC907',
};

const CardContainer = styled.div<{
  color: string;
}>`
  display: flex;
  flex-direction: column;
  background-color: ${(props) => props.color || cardColorMapping.default};
  /* align-items: center; */
  min-width: 200px;
  min-height: 300px;
  border-radius: 20px;
  /* justify-content: center; */
  margin: 10px;
  -webkit-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  -moz-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
`;
const Poster = styled.div`
  border-radius: 10px;
  width: 150px;
  margin: 0 auto;
`;
const PosterImage = styled.div<{
  imgURL: string;
}>`
  width: 150px;
  height: 221px;
  border-radius: 10px;
  background-image: url(${(props) => props.imgURL});
  background-repeat: no-repeat;
  background-size: 100% 100%;
`;
const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding: 10px 10px 0px 10px;
`;
const CardFooter = styled.div`
  width: 100%;
  padding-top: 7px;
`;

interface ContextProps {
  Title: string;
  Poster: string;
  averageRatings: { avrRat: number };
}

function Card() {
  //const { Title, Poster: posterURL } = props;
  const [backgroundColor, setBgColor] = useState(cardColorMapping.default);

  //register setColor once and dont re-render
  const setColor = useCallback(({ watch, favorite }) => {
    switch (true) {
      case !watch && !favorite:
        setBgColor(cardColorMapping.default);
        break;
      case watch && !favorite:
        setBgColor(cardColorMapping.watchedNotSaved);
        break;
      case !watch && favorite:
        setBgColor(cardColorMapping.savedNotWatched);
        break;
      case watch && favorite:
        setBgColor(cardColorMapping.watchedAndSaved);
        break;
      default:
        break;
    }
  }, []);

  const calculateAvarageRatings = (Ratings: {}[]) => {
    const percentageValue: number[] = [];

    const overAllPerc = Ratings.map(
      ({ Value }: { Value: string }) => Value
    ).reduce((avaragePerc, rating, index) => {
      if (rating.includes('/')) {
        const [all, ratio] = rating.split('/').map((value) => Number(value));
        const calcPercentage = (all / ratio) * 100;
        percentageValue.push(calcPercentage);
      }

      if (rating.includes('%')) {
        const value = rating.match(/\d+/)[0];
        const percentage = Number(value);
        percentageValue.push(percentage);
      }

      if (index === Ratings.length - 1) {
        const sumPercentage = percentageValue.reduce(
          (acc, value) => acc + value
        );

        avaragePerc = sumPercentage / Ratings.length;
        return avaragePerc;
      }
    }, 0);

    return overAllPerc;
  };

  return (
    <MoviesContext.Consumer>
      {(contextArray) =>
        contextArray.map(({ Title, Poster: posterURL, Ratings }) => (
          <CardContainer color={backgroundColor} key={Title}>
            <CardHeader>
              <IconsStateManagement callToActionCardColor={setColor} />
            </CardHeader>
            <Poster>
              <PosterImage imgURL={posterURL} />
            </Poster>
            <CardFooter>
              {/*has to be between 0-100 %*/}
              <RatingStarComp rating={calculateAvarageRatings(Ratings)} />
            </CardFooter>
          </CardContainer>
        ))
      }
    </MoviesContext.Consumer>
  );
}

export default Card;
