import React from 'react';
import styled from 'styled-components';
import StarIcon from '@material-ui/icons/Star';
import StarHalfIcon from '@material-ui/icons/StarHalf';
import StarOutlineIcon from '@material-ui/icons/StarOutline';

const RatingContainer = styled.div`
  position: relative;
  border-radius: 10px;
  width: 150px;
  margin: 0 auto;
  background-color: orange;
`;
const RatingSymbol = styled.div`
  background-color: white;
  margin: 0 auto;
  position: absolute;
  mix-blend-mode: screen;
`;
const symbolMap = {
  star: '&#9733;',
};
interface PropsInt {
  rating: number;
}

function Rating(props: PropsInt) {
  const { rating } = props;

  const allStars = 5;
  const calculateRating = String(rating).split('.');
  const wholeStars = Number(calculateRating[0]);
  const halfStar = Number(calculateRating[1]);
  const hashalfStar = (halfStar: number) => (halfStar === 0 ? 0 : 1);
  const emptyStars = allStars - wholeStars;

  const displayStars = () => {
    const startMap = {
      wholeStar: <StarIcon />,
      halfStar: <StarHalfIcon />,
      emptyStar: <StarOutlineIcon />,
    };
  };

  return (
    <RatingContainer>
      <RatingSymbol>
        {[1, 2, 3, 4, 5].map((symbol) => (
          <StarIcon />
        ))}
      </RatingSymbol>
    </RatingContainer>
  );
}

export default Rating;
