import React, { useState } from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';

// custom
const InputComp = (props: { key: string }) => <input type="text" {...props} />;

const style: React.CSSProperties = {
  display: 'flex',
  flexDirection: 'column',
  padding: '10px',
  border: '1px dotted green',
};
/* .filter((e, i) => i > 4)} */
const all = Object.entries(generateFilters());
const displayed = all.filter(
  ([key, value], i) =>
    i < 5 && (
      <>
        <input type="radio" />
        {`${key}(${value})`}
      </>
    )
);
const hidden = all.filter(
  ([key, value], i) =>
    i > 5 && (
      <>
        <input type="radio" />
        {`${key}(${value})`}
      </>
    )
);
let prev;
let calc;
const calculateHeight = (sent: { name: string; value: number }) => {
  calc = { ...sent };
  if (prev) {
    calc = { ...prev, ...sent };
    prev = { ...sent };
  }

  console.log('calc', calc);

  /* const ss = Object.entries({...calc}).reduce(
    (all, [key, value], i) => {
      if (key === 'value') {
        all = all + value;
        return all;
      }
      return all;
    },
    0
  );

  console.log('gagadgegerg', ss); */
};

const FilterContainer = styled.div`
  displahy: flex;
  flexdirection: column;
`;
function FiltersGroup() {
  const [height, setHeight] = useState(0);
  return (
    <FilterContainer>
      <div>
        <Accordion calculateHeight={calculateHeight} name="greg">
          <div style={{ height }}>{displayed} </div>
          <Accordion
            calculateHeight={calculateHeight}
            name="mike"
            nestedAccordion
          >
            <div>{hidden}</div>
          </Accordion>
        </Accordion>
      </div>
      <div>
        <Accordion calculateHeight={calculateHeight} name="greg">
          <div>{displayed} </div>
          <Accordion
            calculateHeight={calculateHeight}
            name="mike"
            nestedAccordion
          >
            <div>{hidden}</div>
          </Accordion>
        </Accordion>
      </div>
    </FilterContainer>
  );
}

export default FiltersGroup;
