import React, { useRef, useState, useMemo, useCallback } from 'react';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import styled from 'styled-components';

//styles
const AccordionHeader = styled.button`
  display: flex;
  width: 100%;
  height: 30px;
  align-items: center;
  padding: 10px;
  justify-content: space-between;
  border: none;
  &.active {
    background-color: orange;
  }
`;

const AccordionContent = styled.div`
  overflow: hidden;
  height: auto;
  width: 100%;
  transition: height 0.6s ease;
`;

const ChevronIcon = styled.span`
  transition: transform 0.6s ease;
  &.rotate {
    transform: rotate(90deg);
  }
`;

interface PropsI {
  children: React.ReactNode;
  open?: boolean;
  shevron?: boolean;
}
function Accordion(props: PropsI) {
  const { children, open = false, shevron = true } = props;

  const refContent = useRef<HTMLDivElement>(null);
  const [isOpen, setOpen] = useState(open ? 'active' : '');

  return (
    <div className="accordion">
      <AccordionHeader
        className={`accordionHeader ${isOpen}`}
        onClick={() => {
          setOpen(!isOpen ? 'active' : '');
        }}
      >
        <span className="accordionTitle">AccordionTitle</span>
        {shevron && (
          <ChevronIcon className={!!isOpen && 'rotate'}>
            <ChevronRightIcon />
          </ChevronIcon>
        )}
      </AccordionHeader>
      <AccordionContent
        ref={refContent}
        style={{
          height: `${
            isOpen !== '' ? Number(refContent.current?.scrollHeight) : 0
          }px`,
        }}
      >
        {children}
      </AccordionContent>
    </div>
  );
}

export default Accordion;
