import React from 'react';
import styled from 'styled-components';
import StarIcon from '@material-ui/icons/Star';
import StarHalfIcon from '@material-ui/icons/StarHalf';
import StarBorderOutlinedIcon from '@material-ui/icons/StarBorderOutlined';

const RatingContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  padding: 0 10px;
`;

interface PropsInt {
  rating: number;
}

function Rating(props: PropsInt) {
  const { rating } = props;

  const allStars = 5;
  const percentage = String((rating * allStars) / 100);
  const calculateRating = percentage.split('.');
  const wholeStars = Number(calculateRating[0]);
  const halfStar = Number(calculateRating[1]) === 0 ? 0 : 1;
  const emptyStars = allStars - wholeStars - halfStar;

  const displayStars = () => {
    const startMap = {
      wholeStar: <StarIcon />,
      halfStar: <StarHalfIcon />,
      emptyStar: <StarBorderOutlinedIcon />,
    };
    const wholeStartsss = Array(wholeStars).fill(startMap['wholeStar']);
    const halfStartsss = Array(halfStar).fill(startMap['halfStar']);
    const emptyStartsss = Array(emptyStars).fill(startMap['emptyStar']);

    return [...wholeStartsss, ...halfStartsss, ...emptyStartsss];
  };
  displayStars();
  return (
    <RatingContainer>
      {displayStars().map((symbol) => symbol)}
      <span>({String(rating)})</span>
    </RatingContainer>
  );
}

export default Rating;
