import React, { useEffect, useState, useCallback, useReducer } from 'react';
import styled from 'styled-components';

//custom
import Card from '../app/contentComponents/Card/Card';
import generalAPIService from '../app/services/apiService';
import FiltersGroup from '../app/contentComponents/FiltersGroup/FiltersGroup';

const Wrapper = styled.div`
  display: flex;
  width: 100%;
`;

const Filters = styled.div`
  display: flex;
  width: 20%;
  background-color: grey;
`;
const Movies = styled.div`
  display: flex;
  width: 80%;
  flex-wrap: wrap;
`;

interface ImoviesResponseFields {
  Title: string;
  Poster: string;
  Ratings: {}[];
  key: string;
  Saved: string;
  Watched: string;
}
const initialState: ImoviesResponseFields = {
  Title: 'string',
  Poster: 'string',
  Ratings: [{}],
  key: 'string',
  Saved: 'string',
  Watched: 'string',
};
export const MoviesContext = React.createContext({} as ImoviesResponseFields[]);
const { Provider, Consumer } = MoviesContext;

const movieReducer = (state: ImoviesResponseFields, action) => {
  switch (action.type) {
    /* eslint-disable no-case-declarations */
    case 'UPDATE':
      return [{ ...state, ...action.payload }];
    default:
      return [{ ...state }];
  }
};

const Home = () => {
  const [loading, isLoading] = useState(true);
  //const [movies, getMovies] = useState([]);
  const [movies, dispatchAction] = useReducer(movieReducer, initialState);

  const reduceData = useCallback((response: {}[]) => {
    return response.map(
      ({ Title, Poster, Ratings, Watched, Saved }: ImoviesResponseFields) => {
        isLoading(false);
        return {
          Title,
          Poster,
          Ratings,
          Watched,
          Saved,
        };
      }
    );
  }, []);

  useEffect(() => {
    isLoading(true);
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      //getMovies(reduceData(result));
      dispatchAction({ type: 'UPDATE', payload: reduceData(result) });
    };

    fetchData();
  }, [reduceData]);

  console.log('gagdg', movies);

  return (
    <main>
      {loading && 'movies are loading'}
      {!loading && (
        <Provider value={movies}>
          <Wrapper>
            <Filters>
              <FiltersGroup />
            </Filters>
            <Movies>
              <Consumer>
                {(moviesObj) =>
                  moviesObj.map((fields, i) => (
                    <Card key={fields.Title} cardId={i} />
                  ))
                }
              </Consumer>
            </Movies>
          </Wrapper>
        </Provider>
      )}
    </main>
  );
};

export default Home;
