        return all;
      }
      return all;
    },
    0
  );

  console.log('gagadgegerg', ss); */
};
function FiltersGroup() {
  return (
    <div>
      <Accordion calculateHeight={calculateHeight} name="greg">
        <div>{displayed} </div>
        <Accordion calculateHeight={calculateHeight} name="mike">
          <div>{hidden}</div>
        </Accordion>
      </Accordion>
    </div>
  );
}

export default FiltersGroup;
