import React, { useState } from 'react';
import styled from 'styled-components';
import { VisibilityIcon } from '@mono-nx-test-with-nextjs/ui';
import VisibilityOffOutlinedIcon from '@material-ui/icons/VisibilityOffOutlined';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';

//custom
import RatingStarComp from '../RatingStarComp/rating';
import wrapperComponentMethod from '../Card/IconCTA/IconAction';

interface CardColorChange {
  color: string;
}

const cardColorMapping = {
  default: '#FFF',
  savedNotWatched: '#ED6606',
  saved: '#049452',
  watchedNotSaved: '#EEC907',
};

const CardContainer = styled.div<CardColorChange>`
  display: flex;
  flex-direction: column;
  background-color: ${(props) => props.color || 'white'};
  /* align-items: center; */
  min-width: 200px;
  min-height: 300px;
  border-radius: 20px;
  /* justify-content: center; */
  margin: 10px;
  -webkit-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  -moz-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
`;
const Poster = styled.div`
  border-radius: 10px;
  width: 150px;
  margin: 0 auto;
`;

const PosterImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding: 10px;
`;

const CardFooter = styled.div`
  width: 100%;
`;

/* ... inside the render or return of your component ... */

interface Props {
  Title: string;
  Poster: string;
}

const VisibilityWatchedIcon = wrapperComponentMethod(
  VisibilityIcon,
  VisibilityOffOutlinedIcon
);
const VisibilitySavedIcon = wrapperComponentMethod(
  FavoriteBorderIcon,
  FavoriteIcon
);

const cardState = { watch: false, favorite: false };

function Card(props: Props) {
  const { Title, Poster: posterURL } = props;

  const [backgroundColor, setBgColor] = useState('orange');

  const setColor = ({ watch, favorite }) => {
    switch (true) {
      case !watch && !favorite:
        setBgColor(cardColorMapping.default);
        break;
      case watch && !favorite:
        setBgColor(cardColorMapping.watchedNotSaved);
        break;
      case !watch && favorite:
        setBgColor(cardColorMapping.savedNotWatched);
        break;

      default:
        return cardColorMapping.default;
        break;
    }
  };
  const callToAction = (state: object) => {
    const cardStateChange = { ...cardState, state };
    setColor(cardStateChange);
  };

  console.log('backgroundColor', backgroundColor);
  return (
    <CardContainer color={backgroundColor}>
      <CardHeader>
        <VisibilityWatchedIcon callToAction={callToAction} icon="watch" />
        <VisibilitySavedIcon callToAction={callToAction} icon="favorite" />
      </CardHeader>
      <Poster>
        <PosterImage src={posterURL} alt={Title} />
      </Poster>
      <CardFooter>
        <RatingStarComp rating={20} />
      </CardFooter>
    </CardContainer>
  );
}

export default Card;
