import React, { useState } from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';

// custom
const InputComp = (props: { key: string }) => <input type="text" {...props} />;

const style: React.CSSProperties = {
  display: 'flex',
  flexDirection: 'column',
  padding: '10px',
  border: '1px dotted green',
};
/* .filter((e, i) => i > 4)} */
const all = Object.entries(generateFilters());
const displayed = all.filter(([key, value], i) => i < 5);
const hidden = all.filter(
  ([key, value], i) =>
    i > 5 && (
      <>
        <input type="radio" />
        {`${key}(${value})`}
      </>
    )
);

const FilterContainer = styled.div`
  displahy: flex;
  flexdirection: column;
`;

function FiltersGroup() {
  return (
    <FilterContainer>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true}>
          <div>
            {displayed.map(([key, value]) => (
              <>
                <input type="radio" />
                {`${key}(${value})`}
              </>
            ))}
          </div>
        </Accordion>
        {hidden.length > 0 && (
          <Accordion>
            <div>{hidden.map((e) => e)}</div>
          </Accordion>
        )}
      </div>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true}>
          <div>{displayed}</div>
        </Accordion>
        {hidden.length > 0 && (
          <Accordion>
            <div>{hidden}</div>
          </Accordion>
        )}
      </div>
    </FilterContainer>
  );
}

export default FiltersGroup;
