import React from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';

// custom
const InputComp = (props: { key: string }) => <input type="text" {...props} />;

const style: React.CSSProperties = {
  display: 'flex',
  flexDirection: 'column',
  padding: '10px',
  border: '1px dotted green',
};
/* .filter((e, i) => i > 4)} */
const all = Object.entries(generateFilters());
const displayed = all.filter(
  ([key, value], i) =>
    i < 5 && (
      <>
        <input type="radio" />
        {`${key}(${value})`}
      </>
    )
);
const hidden = all.filter(
  ([key, value], i) =>
    i > 5 && (
      <>
        <input type="radio" />
        {`${key}(${value})`}
      </>
    )
);
const calculateHeight = ({ name: string, value: number }) => {
  const ss = Object.entries({ name: string, value: number }).reduce(
    (all, [key, value], i) => {
      if (key === 'value') {
        all = all + value;
        return all;
      }
      return all;
    },
    0
  );
};
console.log('hiddent', hidden);
function FiltersGroup() {
  return (
    <div>
      <Accordion calculateHeight={calculateHeight} name="greg">
        <div>{displayed} </div>
        <Accordion calculateHeight={calculateHeight} name="mike">
          <div>{hidden}</div>
        </Accordion>
      </Accordion>
    </div>
  );
}

export default FiltersGroup;
