import React, { useState } from 'react';
import styled from 'styled-components';

const IconContainer = styled.div`
  cursor: pointer;
`;
interface PropsInt {
  callToAction: (state: object) => void;
  icon: string;
  obj: { watch: boolean; favorite: boolean };
}
const wrapperComponentMethod = (Icon1: React.FC, Icon2: React.FC) => ({
  callToAction,
  icon,
  obj,
}: PropsInt) => {
  const [iconVisiblity, setVisible] = useState(false);
  const iconCTA = () => {
    setVisible(!iconVisiblity);

    callToAction({ ...obj, [icon]: !iconVisiblity });
  };
  return (
    <IconContainer onClick={iconCTA}>
      {!iconVisiblity && <Icon1 />}
      {iconVisiblity && <Icon2 />}
    </IconContainer>
  );
};

export default wrapperComponentMethod;
