import React, { useState } from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';

const all = Object.entries(generateFilters());
const displayed = all.slice(0, 5);
const hidden = all.slice(5, all.length);

const FilterContainer = styled.div`
  display: flex;
  flex-direction: column;
`;
const AccordionMore = styled.div`
  padding: 10px;
`;

function FiltersGroup() {
  return (
    <FilterContainer>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true} title={'TYPE'}>
          <div>
            {displayed.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </div>
        </Accordion>
        {hidden.length > 0 && (
          <Accordion title={'...more'} directional={true} shevron={false}>
            {hidden.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </Accordion>
        )}
      </div>
      <div className="accordion">
        <Accordion open shevron={false} clickDisable={true} title={'STATUS'}>
          <div>
            {displayed.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </div>
        </Accordion>
        {hidden.length > 0 && (
          <Accordion title={'...more'} directional={true} shevron={false}>
            {hidden.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </Accordion>
        )}
      </div>
    </FilterContainer>
  );
}

export default FiltersGroup;
