import React, { useState } from 'react';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';

// custom
const InputComp = (props: { key: string }) => <input type="text" {...props} />;

const all = Object.entries(generateFilters());
const displayed = all.slice(0, 5);
const hidden = all.slice(5, 50);

const FilterContainer = styled.div`
  display: flex;
  flexdirection: column;
`;
const AccordionMore = styled.div`
  padding: 10px;
`;

function FiltersGroup() {
  return (
    <FilterContainer>
      <Accordion open shevron={false} clickDisable={true}>
        <div>
          {displayed.map(([key, value]) => (
            <div key={key}>
              <input type="radio" />
              {`${key}(${value})`}
            </div>
          ))}
        </div>
      </Accordion>
      {hidden.length > 0 && (
        <AccordionMore>
          <Accordion>
            {hidden.map(([key, value]) => (
              <div key={key}>
                <input type="radio" />
                {`${key}(${value})`}
              </div>
            ))}
          </Accordion>
        </AccordionMore>
      )}
      <Accordion open shevron={false} clickDisable={true}>
        <div>
          {displayed.map(([key, value]) => (
            <div key={key}>
              <input type="radio" />
              {`${key}(${value})`}
            </div>
          ))}
        </div>
      </Accordion>
      {hidden.length > 0 && (
        <Accordion>
          {hidden.map(([key, value]) => (
            <div key={key}>
              <input type="radio" />
              {`${key}(${value})`}
            </div>
          ))}
        </Accordion>
      )}
    </FilterContainer>
  );
}

export default FiltersGroup;
