import React, { useState } from 'react';
import styled from 'styled-components';
//import useIconsState from './useIconsState';

const IconContainer = styled.div`
  cursor: pointer;
`;
interface PropsInt {
  callToAction: (state: object) => void;
  icon: string;
  obj: { watch: boolean; favorite: boolean };
  cardNumber: string;
}

const wrapperComponentMethod = (Icon1: React.FC, Icon2: React.FC) => ({
  callToAction,
  icon,
  obj,
  cardNumber,
}: PropsInt) => {
  const [iconVisiblity, setVisible] = useState(false);
  //const { iconsState, setIconsState } = useIconsState(cardNumber);
  const iconCTA = () => {
    setVisible(!iconVisiblity);

    /*  setIconsState((prev) => {
      return { ...prev, ...iconsState };
    }); */
    callToAction({ [cardNumber]: { [icon]: !iconVisiblity } });
  };
  return (
    <IconContainer onClick={iconCTA}>
      {!iconVisiblity && <Icon1 />}
      {iconVisiblity && <Icon2 />}
    </IconContainer>
  );
};

export default wrapperComponentMethod;
