import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

//custom
import Card from './Card/Card';
import generalAPIService from './services/apiService';

const Movies = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Home = () => {
  const [movies, getMovies] = useState([]);

  interface ImoviesResponseFields {
    Title: string;
    Poster: string;
    Ratings: {}[];
    key: string;
  }
  const calculateAvarageRatings = (Ratings: {}[]) => {
    const percentageValue: number[] = [];

    const overAllPerc = Ratings.map(
      ({ Value }: { Value: string }) => Value
    ).reduce((avaragePerc, rating, index) => {
      console.log('rating.....', rating);

      if (rating.includes('/')) {
        const [all, ratio] = rating.split('/').map((value) => Number(value));
        console.log([all, ratio]);
        console.log('[all, ratio]', all / ratio);
        const calcPercentage = (all / ratio) * 100;
        percentageValue.push(calcPercentage);
      }

      if (rating.includes('%')) {
        const value = rating.match(/\d+/)[0];
        const percentage = Number(value);
        console.log('percentage', percentage);
        percentageValue.push(percentage);
      }

      console.log('percentageValueppppp.....', percentageValue);
      if (index === Ratings.length - 1) {
        const sumPercentage = percentageValue.reduce(
          (acc, value) => acc + value
        );
        console.log('percentageValue', percentageValue);
        console.log('avaragePerc', sumPercentage);
        avaragePerc = sumPercentage / Ratings.length;
        return avaragePerc;
      }
    }, 0);

    return overAllPerc;
  };
  const reduceData = (response: {}[]) => {
    return response.map(({ Title, Poster, Ratings }: ImoviesResponseFields) => {
      const averageRatings = calculateAvarageRatings([
        { Source: 'Internet Movie Database', Value: '8.1/10' },
        { Source: 'Rotten Tomatoes', Value: '85%' },
        { Source: 'Metacritic', Value: '69/100' },
      ]);

      console.log('averageRatings', averageRatings);

      return { Title, Poster, averageRatings };
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      console.log('result', result);
      getMovies(reduceData(result));
    };

    fetchData();
  }, []);

  return (
    <main>
      <Movies>
        {movies.map((fields) => (
          <Card {...fields} key={fields.Title} />
        ))}
      </Movies>
    </main>
  );
};

export default Home;
