import React, { useEffect, useState, useCallback } from 'react';
import styled from 'styled-components';

//custom
import Card from './Card/Card';
import generalAPIService from './services/apiService';

const Movies = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const Home = () => {
  const [loading, isLoading] = useState(true);
  const [movies, getMovies] = useState([]);

  interface ImoviesResponseFields {
    Title: string;
    Poster: string;
    Ratings: {}[];
    key: string;
  }
  const calculateAvarageRatings = (Ratings: {}[]) => {
    const percentageValue: number[] = [];

    const overAllPerc = Ratings.map(
      ({ Value }: { Value: string }) => Value
    ).reduce((avaragePerc, rating, index) => {
      if (rating.includes('/')) {
        const [all, ratio] = rating.split('/').map((value) => Number(value));
        const calcPercentage = (all / ratio) * 100;
        percentageValue.push(calcPercentage);
      }

      if (rating.includes('%')) {
        const value = rating.match(/\d+/)[0];
        const percentage = Number(value);
        percentageValue.push(percentage);
      }

      if (index === Ratings.length - 1) {
        const sumPercentage = percentageValue.reduce(
          (acc, value) => acc + value
        );

        avaragePerc = sumPercentage / Ratings.length;
        return avaragePerc;
      }
    }, 0);

    return overAllPerc;
  };
  const reduceData = useCallback((response: {}[]) => {
    const averageRatings: { avrRat: number }[] = [];
    return response.map(({ Title, Poster, Ratings }: ImoviesResponseFields) => {
      averageRatings.push({ avrRat: calculateAvarageRatings(Ratings) });
      isLoading(false);
      return { Title, Poster, averageRatings };
    });
  }, []);

  useEffect(() => {
    isLoading(true);
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      getMovies(reduceData(result));
    };

    fetchData();
  }, [reduceData]);

  return (
    <main>
      {loading && 'movies are loading'}
      <Movies>
        {movies.map((fields, i) => (
          <Card {...fields} key={fields.Title} />
        ))}
      </Movies>
    </main>
  );
};

export default Home;
