import React, { useRef, useState, useMemo } from 'react';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import styled from 'styled-components';

//styles
const AccordionHeader = styled.button`
  display: flex;
  width: 100%;
  height: 30px;
  align-items: center;
  padding: 10px;
  justify-content: space-between;
  border: none;
  &.active {
    background-color: orange;
  }
`;

const AccordionContent = styled.div`
  overflow: hidden;
  width: 100%;
  transition: height 0.6s ease;
`;

const ChevronIcon = styled.span`
  transition: transform 0.6s ease;
  &.rotate {
    transform: rotate(90deg);
  }
`;

interface PropsI {
  children: React.ReactNode;
  forceHeight?: number;
  name: string;
  calculateHeight: ({ name: string, value: number }) => void;
}
function Accordion(props: PropsI) {
  const { children, calculateHeight, name } = props;

  const refContent = useRef<HTMLDivElement>(null);

  const [isOpen, setOpen] = useState('');

  const elementHeight = refContent.current?.scrollHeight;
  if (elementHeight !== 0 || isNaN(elementHeight)) {
    calculateHeight({ name, value: elementHeight });
  }

  const openAccordion = () => {
    setOpen(!isOpen ? 'active' : '');
  };

  return (
    <div className="accordion">
      <AccordionHeader
        className={`accordionHeader ${isOpen}`}
        onClick={openAccordion}
      >
        <span className="accordionTitle">AccordionTitle</span>
        <ChevronIcon className={!!isOpen && 'rotate'}>
          <ChevronRightIcon />
        </ChevronIcon>
      </AccordionHeader>
      <AccordionContent
        ref={refContent}
        style={{
          height: `${
            isOpen !== '' ? Number(refContent.current?.scrollHeight) * 2 : 0
          }px`,
        }}
      >
        {children}
      </AccordionContent>
    </div>
  );
}

export default Accordion;
