let actorsStringData = '';
let trackIndex = 1;
const generateFilters = (stringData: string, allMovies: number) => {
  actorsStringData += stringData + ',';

  function findDuplicate(arr: string[]) {
    const object = {};
    arr.forEach(function (item: string) {
      const trimStirng = item.trim();
      if (!object[trimStirng]) object[trimStirng] = 0;
      object[trimStirng] += 1;
    });
    return object;
  }

  if (trackIndex === allMovies) {
    const trimLastComa = actorsStringData.trim().replace(/,$/, '');
    const allActors = [...trimLastComa.split(',')];
    trackIndex = 1;
    actorsStringData = '';
    return findDuplicate(allActors);
  } else {
    trackIndex++;
  }
};

export { generateFilters };
