import React, { useState } from 'react';
import styled from 'styled-components';
import { VisibilityIcon } from '@mono-nx-test-with-nextjs/ui';
import VisibilityOffOutlinedIcon from '@material-ui/icons/VisibilityOffOutlined';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';

//custom
import RatingStarComp from '../RatingStarComp/Rating';
import wrapperIconHOC from '../Card/IconCTA/IconAction';

interface CardColorChange {
  color: string;
}

const cardColorMapping = {
  default: '#FFF',
  savedNotWatched: '#ED6606',
  watchedAndSaved: '#049452',
  watchedNotSaved: '#EEC907',
};

const CardContainer = styled.div<CardColorChange>`
  display: flex;
  flex-direction: column;
  background-color: ${(props) => props.color || cardColorMapping.default};
  /* align-items: center; */
  min-width: 200px;
  min-height: 300px;
  border-radius: 20px;
  /* justify-content: center; */
  margin: 10px;
  -webkit-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  -moz-box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
  box-shadow: -1px 0px 21px -5px rgba(0, 0, 0, 0.53);
`;
const Poster = styled.div`
  border-radius: 10px;
  width: 150px;
  margin: 0 auto;
`;

const PosterImage = styled.img`
  max-width: 100%;
  height: auto;
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding: 10px;
`;

const CardFooter = styled.div`
  width: 100%;
`;

/* ... inside the render or return of your component ... */

interface Props {
  Title: string;
  Poster: string;
  cardNumber: string;
}

const VisibilityWatchedIcon = wrapperIconHOC(
  VisibilityIcon,
  VisibilityOffOutlinedIcon
);
const VisibilitySavedIcon = wrapperIconHOC(FavoriteBorderIcon, FavoriteIcon);

//build card color object for each card

const cardState = { watch: false, favorite: false };
let previousState: { watch: boolean; favorite: boolean };
class Card extends React.Component<Props> {
  constructor(props: Props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { backgroundColor: cardColorMapping.default };
  }

  //const [backgroundColor, setBgColor] = useState(cardColorMapping.default);

  setColor = ({ watch, favorite }) => {
    switch (true) {
      case !watch && !favorite:
        //setBgColor(cardColorMapping.default);
        console.log('notWatchednotSaved');
        break;
      case watch && !favorite:
        //setBgColor(cardColorMapping.watchedNotSaved);
        console.log('watchedNotSaved');
        break;
      case !watch && favorite:
        //setBgColor(cardColorMapping.savedNotWatched);
        console.log('savedNotWatched');
        break;
      case watch && favorite:
        //setBgColor(cardColorMapping.watchedAndSaved);
        console.log('watchedAndSaved');
        break;
    }
  };

  callToAction = (state: object) => {
    let cardStateChange: { watch: boolean; favorite: boolean };
    if (!previousState) {
      cardStateChange = { ...cardState, ...state };
    } else {
      cardStateChange = { ...previousState, ...state };
    }
    previousState = cardStateChange;

    console.log('state ', cardStateChange);
    //setColor(cardStateChange);
  };

  render() {
    const { Poster: posterURL, Title } = this.props;
    return (
      <CardContainer color={cardColorMapping.default}>
        <CardHeader>
          <VisibilityWatchedIcon
            callToAction={this.callToAction}
            icon="watch"
          />
          <VisibilitySavedIcon
            callToAction={this.callToAction}
            icon="favorite"
          />
        </CardHeader>
        <Poster>
          <PosterImage src={posterURL} alt={Title} />
        </Poster>
        <CardFooter>
          <RatingStarComp rating={20} />
        </CardFooter>
      </CardContainer>
    );
  }
}

export default Card;
