const generateFilters = () => {
  const actors = 'Harrison Ford, Rutger Hauer, Sean Young, Edward James Olmos';
  const actors2 =
    'Harrison Ford, Russell Crowe, Joaquin Phoenix, Connie Nielsen, Oliver Reed';
  const actors3 = 'Harrison Ford';

  //first combine actors results

  const allActors = [
    ...actors.split(','),
    ...actors2.split(','),
    ...actors3.split(','),
  ];
  const filteredActors = [];
  let duplicatesdActors = [];
  //to avoid chacking if i===array.lenght -1 consider making promise to get all values
  //and continue with the code

  /* const ss = allActors.map((actor) => {
    //duplicates
    if (filteredActors.includes(actor)) {
      console.log('duplicate actor', actor);
      duplicatesdActors.push(actor);
    }

    if (duplicatesdActors.length > 0) {
      filteredActors.push(duplicatesdActors);
      //do it again for the first actor on first interation
      if (allActors.includes(duplicatesdActors[0])) {
        console.log('duplicate actor', actor);
        duplicatesdActors.push(actor);
      }
      duplicatesdActors = [];
    } else {
      filteredActors.push(actor);

      console.log('filteredActors', filteredActors);
    }
  }); */

  function findDuplicate(arra1) {
    const object = {};
    arra1.forEach(function (item: string) {
      const trimStirng = item.trim();
      if (!object[trimStirng]) object[trimStirng] = 0;
      object[trimStirng] += 1;
    });
    return object;
  }

  console.log('duplicate', findDuplicate(allActors));
};

export { generateFilters };
