import React from 'react';
import styled from 'styled-components';
import StarIcon from '@material-ui/icons/Star';
import StarHalfIcon from '@material-ui/icons/StarHalf';
import StarBorderOutlinedIcon from '@material-ui/icons/StarBorderOutlined';

const RatingContainer = styled.div`
  position: relative;
  border-radius: 10px;
  width: 150px;
  margin: 0 auto;
  background-color: orange;
`;
const RatingSymbol = styled.div`
  background-color: white;
  margin: 0 auto;
  position: absolute;
  mix-blend-mode: screen;
`;
const symbolMap = {
  star: '&#9733;',
};
interface PropsInt {
  rating: number;
}

function Rating(props: PropsInt) {
  const { rating } = props;

  const allStars = 5;
  const calculateRating = String(rating).split('.');
  const wholeStars = Number(calculateRating[0]);
  const halfStar = Number(calculateRating[1]);
  const hasHalfStar = (halfStar: number) => (halfStar === 0 ? 0 : 1);
  const emptyStars = allStars - wholeStars - hasHalfStar(halfStar);

  const displayStars = () => {
    const startMap = {
      wholeStar: <StarIcon />,
      halfStar: <StarHalfIcon />,
      emptyStar: <StarBorderOutlinedIcon />,
    };
    const wholeStartsss = Array(allStars).fill(0, 3);
    console.log('wholeStartsss', wholeStartsss);
    return;
  };
  displayStars();
  return (
    <RatingContainer>
      <RatingSymbol>
        {[1, 2, 3, 4, 5].map((symbol) => (
          <StarIcon />
        ))}
      </RatingSymbol>
    </RatingContainer>
  );
}

export default Rating;
