import React, { useState, useContext } from 'react';
import CheckBoxOutlineBlankOutlinedIcon from '@material-ui/icons/CheckBoxOutlineBlankOutlined';
import CheckBoxOutlinedIcon from '@material-ui/icons/CheckBoxOutlined';

//custom
import Accordion from '../Accordion/Accordion';
import { generateFilters } from '../FiltersGroup/generateFilters';
import styled from 'styled-components';
import wrapperIconHOC from '../../contentComponents/IconCTA/IconAction';
import { MoviesContext } from '../../../pages/index';

/* const all = Object.entries(generateFilters());
const displayed1 = all.slice(0, 5);
const hidden1 = all.slice(5, all.length); */

const FilterContainer = styled.div`
  display: flex;
  flex-direction: column;
  color: white;
`;

const CheckBox = wrapperIconHOC(
  CheckBoxOutlineBlankOutlinedIcon,
  CheckBoxOutlinedIcon
);

function FiltersGroup() {
  const { movies, updateState } = useContext(MoviesContext);
  /* const { Watched, Saved, Title, Poster: PosterURL, Ratings } = [...movies];
  const movieStatus = {
    watch: JSON.parse(Watched.toLowerCase()),
    favorite: JSON.parse(Saved.toLowerCase()),
  }; */

  const callToAction = (state) => {
    const { yes, no } = state;
    updateState(yes ? 'True' : 'False');
  };
  console.log('movies', movies);

  /* const displayFilters = (ArrOfFilters:[])=>(

  {displayed:ArrOfFilters.slice(0, 5),
  hidden:ArrOfFilters.slice(5, allFilteres.length)}
 ) */
  console.log();
  const watched = Object.entries({
    YES: movies.filter(({ Watched }) => Watched === 'True').length,
    NO: movies.filter(({ Watched }) => Watched === 'False').length,
  });

  const type = Object.entries({
    MOVIE: movies.filter(({ Type }) => Type === 'movie').length,
    SERIES: movies.filter(({ Type }) => Type === 'series').length,
  });

  interface ImoviesResponseFields {
    Title: string;
    Poster: string;
    Ratings: {}[];
    Saved: string;
    Watched: string;
    Actors: string;
    Type: string;
  }
  type Actors = Pick<ImoviesResponseFields, 'Actors'>;
  //actors
  const actorsFilters = movies.reduce((obj, { Actors }) => {
    return generateFilters(Actors, movies.length);
  }, {});

  console.log('actorsFilters', actorsFilters);

  /*  const displayed = watched.slice(0, 5);
  const hidden = watched.slice(5, all.length); */

  /* const FilterLabel = {
    watched,
    type,
    actors:generateFilters()
  }; */

  const ss = true;
  return (
    <>
      {ss && '...loadin'}
      <FilterContainer>
        {['watched'].map((title) => (
          <div className="accordion">
            <Accordion open shevron={false} clickDisable={true} title={title}>
              <div>
                {[].map(([key, value]) => (
                  <div key={key}>
                    <CheckBox callToAction={callToAction} icon="yes" />
                    {`${key}(${value})`}
                  </div>
                ))}
              </div>
            </Accordion>
            {[].length > 0 && (
              <Accordion title={'...more'} directional={true} shevron={false}>
                {[].map(([key, value]) => (
                  <div key={key}>
                    <CheckBox callToAction={callToAction} icon="no" />
                    {`${key}(${value})`}
                  </div>
                ))}
              </Accordion>
            )}
          </div>
        ))}
      </FilterContainer>
    </>
  );
}

//make sure it render only the amount of movies.lenght
export default React.memo(FiltersGroup);
