import React, { useEffect, useState, useCallback } from 'react';
import styled from 'styled-components';

//custom
import Card from './Card/Card';
import generalAPIService from './services/apiService';

const Wrapper = styled.div`
  display: flex;
  width: 100%;
`;

const Filters = styled.div`
  display: flex;
  width: 20%;
  background-color: grey;
`;
const Movies = styled.div`
  display: flex;
  width: 80%;
  flex-wrap: wrap;
`;

interface ImoviesResponseFields {
  Title: string;
  Poster: string;
  Ratings: {}[];
  key: string;
  Saved: string;
  Watched: string;
}
export const MoviesContext = React.createContext({} as ImoviesResponseFields[]);
const { Provider } = MoviesContext;

const Home = () => {
  const [loading, isLoading] = useState(true);
  const [movies, getMovies] = useState([]);

  const calculateAvarageRatings = (Ratings: {}[]) => {
    const percentageValue: number[] = [];

    const overAllPerc = Ratings.map(
      ({ Value }: { Value: string }) => Value
    ).reduce((avaragePerc, rating, index) => {
      if (rating.includes('/')) {
        const [all, ratio] = rating.split('/').map((value) => Number(value));
        const calcPercentage = (all / ratio) * 100;
        percentageValue.push(calcPercentage);
      }

      if (rating.includes('%')) {
        const value = rating.match(/\d+/)[0];
        const percentage = Number(value);
        percentageValue.push(percentage);
      }

      if (index === Ratings.length - 1) {
        const sumPercentage = percentageValue.reduce(
          (acc, value) => acc + value
        );

        avaragePerc = sumPercentage / Ratings.length;
        return avaragePerc;
      }
    }, 0);

    return overAllPerc;
  };
  const reduceData = useCallback((response: {}[]) => {
    //const averageRatingsAll: { avrRat: number }[] = [];
    //let averageRatings: { avrRat: number };
    return response.map(
      ({ Title, Poster, Ratings, Watched, Saved }: ImoviesResponseFields) => {
        //averageRatingsAll.push({ avrRat: calculateAvarageRatings(Ratings) });
        //averageRatings = averageRatingsAll[i];
        isLoading(false);
        return {
          Title,
          Poster,
          Ratings,
          Watched,
          Saved,
        };
      }
    );
  }, []);

  useEffect(() => {
    isLoading(true);
    const fetchData = async () => {
      const result = await generalAPIService.get('/api/movies');
      getMovies(reduceData(result));
    };

    fetchData();
  }, [reduceData]);

  return (
    <main>
      {loading && 'movies are loading'}
      <Provider value={movies}>
        <Wrapper>
          <Filters />
          <Movies>
            {movies.map((fields, i) => (
              <Card key={fields.Title} cardId={i} />
            ))}
          </Movies>
        </Wrapper>
      </Provider>
    </main>
  );
};

export default Home;
