let actorsStringData = '';
let trackIndex = 1;
const generateFilters = (stringData: string, allMovies: number) => {
  //first combine actors results
  const isComaSeperated = stringData.trim().includes(',') ? '' : ',';
  actorsStringData += stringData + isComaSeperated;

  console.log('actorsStringData', actorsStringData.trim());

  //to avoid chacking if i===array.lenght -1 consider making promise to get all values
  //and continue with the cod

  function findDuplicate(arr: string[]) {
    const object = {};
    arr.forEach(function (item: string) {
      const trimStirng = item.trim();
      if (!object[trimStirng]) object[trimStirng] = 0;
      object[trimStirng] += 1;
    });
    return object;
  }

  console.log('trackIndex', trackIndex);
  if (trackIndex === allMovies) {
    const trimLastComa = actorsStringData.trim().replace(/,$/, '');
    const allActors = [...trimLastComa.split(',')];
    console.log('allActors', allActors);
    const ss = findDuplicate(allActors);

    return ss;
  } else {
    trackIndex++;
    //return;
  }
};

export { generateFilters };
